package status

import com.soywiz.krypto.md5
import io.ktor.http.Url

data class Gravatar(
    val email: String?,
    val size: Int = 64,
) {
    val url: Url
        get() = email?.encodeToByteArray()?.md5()?.hex
            ?.let { Url("https://www.gravatar.com/avatar/$it?s=$size") }
            ?: Url(JOHN)
}

